vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float theta = atan2(uv.y, uv.x) + iTime * 3.0;
    float r = length(uv);

    // Create rotating color wheel effect
    vec3 color = hsl2rgb(vec3(
        (theta / (2.0 * PI)) + sin(iTime * 0.5) * 0.5,
        1.0,
        0.5 + 0.5 * turbulent(uv * 4.0)
    ));

    // Add pulsating center effect
    float pulse = sin(iTime) * 0.5 + 0.5;
    color = mix(color, vec3(1.0), pulse * (1.0 - r));

    return vec4(color, 1.0);
}
