vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates over time
    float t = iTime * 0.5;
    vec2 rotatedUV = vec2(
        cos(t)*uv.x - sin(t)*uv.y,
        sin(t)*uv.x + cos(t)*uv.y
    );

    // Create geometric pattern with noise and turbulence
    float circleDist = length(rotatedUV);
    float pattern = mod(circleDist * 10.0 + t, 1.0);

    // Add noise texture
    vec2 noiseScale = rotatedUV * 5.0;
    float noiseVal = noise(noiseScale) * 0.5 + 0.5;
    float turbulence = turbulent(vec3(rotatedUV, t)) * 0.5 + 0.5;

    // Combine pattern and noise
    pattern = mix(pattern, noiseVal, turbulence);

    // Create RGB shifts with time
    vec3 color = hsl2rgb(vec3(
        mod(circleDist * 10.0 + t * 2.0, 1.0),
        0.7,
        0.5
    ));

    // Add geometric transformations
    float angle = atan2(rotatedUV.y, rotatedUV.x);
    color *= sin(angle * 4.0 + t) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
