vec4 Effect2(vec2 uv) {
    // Create base frequencies using time
    float freq1 = sin(iTime * 0.5);
    float freq2 = cos(iTime);

    // Rotate and transform UV coordinates
    vec2 st = uv;
    st.x *= freq1 + 0.5;
    st.y *= freq2 + 0.5;

    // Add some turbulence to create interesting patterns
    st += turbulent(st * 10.0) / 50.0;

    // Create color cycling effect using HSL
    float h = sin(iTime * 0.3 + st.x * 2.0) * 0.5 + 0.5;
    float s = sin(iTime * 0.4 + st.y * 3.0) * 0.5 + 0.5;
    float l = sin(iTime * 0.6 + st.x * st.y * 2.0) * 0.5 + 0.5;

    // Convert HSL to RGB
    vec3 color = hsl2rgb(vec3(h, s, l));

    // Add some sine wave modulation
    float mod = sin(iTime + st.x * 10.0);
    color = mix(color, vec3(mod), 0.5);

    return vec4(color, 1.0);
}
