vec4 Effect2(vec2 uv) {
    // Create rotating noise patterns with color shifts over time

    // Speed up time for more dynamic movement
    float time = iTime * 0.5;

    // Rotate coordinates based on time
    vec2 rotatedUv = vec2(
        cos(time) * uv.x - sin(time) * uv.y,
        sin(time) * uv.x + cos(time) * uv.y
    );

    // Add noise layers with different rotations and speeds
    float noise1 = noise(vec3(rotatedUv * 4.0, time));
    float noise2 = noise(vec3(uv * 8.0 - rotatedUv * 4.0, time + 1.0));

    // Combine noise patterns with turbulence
    float pattern = turbulent(rotatedUv * 4.0) * 0.5 +
                   turbulent(uv * 2.0 + rotatedUv * 3.0) * 0.3;

    // Add color shifts using HSL conversion
    vec3 color = hsl(
        sin(time * 0.7) * 0.5 + 0.5,   // Hue variation over time
        1.0,                            // Saturation
        pattern * 0.6 + 0.4             // Lightness based on noise
    );

    return vec4(color, 1.0);
}
