vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 2.0;
    // Create rotating pattern using noise and turbulence
    float n1 = noise(vec3(theta, r * 10.0, iTime));
    float n2 = noise(vec3(theta * 2.0, r * 50.0, iTime * 0.5));
    float t = turbulent(vec2(r * 10.0 + iTime, theta));
    // Create color transitions
    vec3 hslColor = vec3(
        mod(theta / 4.0 + iTime * 0.5, 1.0),
        0.7,
        0.5 + t * 0.5
    );
    // Mix noise values into the color
    hslColor.rg += n1 * 0.3;
    hslColor.b += n2 * 0.4;
    return vec4(hsl2rgb(hslColor), 1.0);
}
