vec4 Effect2(vec2 uv) {
    // Create a coordinate system centered at (0,0)
    vec2 st = uv * 2.0 - 1.0;

    // Add time-based rotation
    float t = iTime * 0.5;
    vec2 rotated = vec2(
        st.x * cos(t) - st.y * sin(t),
        st.x * sin(t) + st.y * cos(t)
    );

    // Create a noise pattern with turbulence
    float n1 = noise(vec3(rotated, iTime * 0.1));
    float n2 = turbulent(rotated * 4.0);

    // Combine noise layers with time-based variation
    float pattern = sin(n1 * 8.0 + t) * cos(n2 * 2.0 + t * 0.5);

    // Create a color palette using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        fract(t * 0.2 + pattern * 0.1) * 360.0,
        0.7 + abs(sin(t)) * 0.3,
        0.5 + abs(cos(t)) * 0.5
    ));

    // Output the final color with some transparency
    return vec4(color, 0.8);
}
