vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 centered = uv - 0.5;
    float radius = length(centered);
    float angle = atan2(centered.y, centered.x);

    // Add time-based rotation and expansion
    float timeScale = iTime * 0.1;
    radius += sin(iTime) * 0.3;
    angle += iTime + turbulent(vec2(radius * 5.0, iTime));

    // Create color variations using HSL
    vec3 hslColor;
    hslColor.x = fract(angle / (pi * 2.0) + timeScale);
    hslColor.y = 1.0 - abs(0.5 - radius) * 2.0;
    hslColor.z = 0.7 + turbulent(vec2(radius * 3.0, iTime)) * 0.3;

    // Convert to RGB and add noise
    vec3 color = hsl2rgb(hslColor);
    color += noise(vec2(angle, radius * 10.0)) * 0.5;

    return vec4(color, 1.0);
}
