vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Generate multiple layers of particles
    for (int i = 0; i < 5; i++) {
        // Scale and offset for different particle sizes
        float scale = pow(1.5, float(i)) * 2.0;
        vec2 st = uv * scale + iTime * 0.5;

        // Add noise to create organic movement patterns
        vec2 pos = vec2(
            sin(iTime * 0.3 + turbulent(st.yy) * 2.0),
            cos(iTime * 0.4 + turbulent(st.xx) * 2.0)
        ) * 0.5;

        // Create a grid of particle points
        vec2 grid = fract(st);
        vec2 center = vec2(0.5, 0.5);
        vec2 toCenter = center - grid;

        // Calculate distance from particle point
        float dist = length(toCenter) * scale;
        float influence = smoothstep(1.0, 0.0, dist);

        // Create color based on position and time
        vec3 hslColor = vec3(
            fract(iTime * 0.2 + grid.x * 2.0),
            0.7,
            0.5 + influence * 0.5
        );
        vec3 rgbColor = hsl2rgb(hslColor);

        // Add noise to color for more dynamic effect
        rgbColor *= 1.0 + noise(vec3(st, iTime)) * 0.3;

        // Accumulate color with alpha based on influence
        color += rgbColor * influence;
    }

    // Final color with glow effect
    vec4 finalColor = vec4(color, max(0.2, length(color) - 0.5));
    return finalColor;
}
