vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Add time-based rotation
    a += iTime * 0.5 + r * 0.5;
    // Create hexagonal pattern using polar coordinates
    float hex = sin(6.0 * a) * 0.5 + 0.5;
    float pattern = step(hex, 0.7);
    // Add noise and turbulence for more complexity
    vec3 noisePos = vec3(uv * 10.0, iTime);
    float turb = turbulent(noisePos);

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(a * 0.5 + iTime, 1.0),
        0.7,
        0.5 + 0.5 * sin(iTime)
    );
    vec3 rgbColor = hsl2rgb(hslColor);
    // Combine pattern and color
    float finalPattern = mix(pattern, turb, 0.5);
    vec3 finalColor = mix(vec3(1.0), rgbColor, finalPattern);
    return vec4(finalColor, 1.0);
}
