vec4 Effect2(vec2 uv) {
    // Create flowing patterns using time-based transformations
    vec2 st = uv * 10.0 + vec2(sin(iTime)*5.0, cos(iTime*0.7)*3.0);

    // Add turbulence for dynamic movement
    float t = turbulent(vec3(st.x + iTime*2.0, st.y + iTime, 0.0));

    // Create color transitions using HSL to RGB conversion
    vec3 col = hsl2rgb(vec3(t * 0.6 + 0.4, 0.8, 0.5));

    // Add secondary color layer for more depth
    float t2 = turbulent(vec3(st.x + iTime*1.5, st.y - iTime, 0.0));
    vec3 col2 = hsl2rgb(vec3(t2 * 0.6 + 0.4, 0.8, 0.7));

    // Mix colors based on noise for smooth transitions
    float blend = noise(vec2(st.x*0.5 + iTime, st.y*0.5)) * 0.5 + 0.5;
    col = mix(col, col2, blend);

    return vec4(col, 1.0);
}
