vec4 Effect2(vec2 uv) {
    // Create rotating pattern using sine functions
    float time = iTime * 0.5;

    // Translate UV to center coordinates
    vec2 st = uv - 0.5;

    // Apply rotation based on time
    float angle = time;
    vec2 rotated = vec2(
        st.x * cos(angle) - st.y * sin(angle),
        st.x * sin(angle) + st.y * cos(angle)
    );

    // Create color variations using HSL
    vec3 color1 = hsl(0.5 * sin(time + 1.0) + 0.5, 
                     0.7, 
                     0.5 * rotated.x * rotated.y + 0.5);

    vec3 color2 = hsl(0.5 * cos(time + 2.0) + 0.5,
                     0.7,
                     0.5 * (sin(rotated.x * 4.0 + time) + 1.0));

    // Combine colors with distance from center
    float dist = length(st);
    vec3 finalColor = mix(color1, color2, sin(dist * 8.0 + time) * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
