vec4 Effect2(vec2 uv) {
    // Time-based frequency and amplitude modulation
    float time = iTime * 0.5;

    // Position transformation with sine waves
    vec2 pos = uv * 10.0 + vec2(sin(time) * 3.0, cos(time) * 3.0);

    // Multiple octaves for complex pattern
    float freq1 = sin(pos.x * 0.5 + time) * 0.5;
    float freq2 = sin(pos.y * 0.5 + time * 0.7) * 0.3;
    float freq3 = sin((pos.x + pos.y) * 0.2 + time * 0.4) * 0.2;

    // Combine frequencies for color
    vec3 color = vec3(freq1, freq2, freq3);

    // HSL to RGB conversion with time-based hue shift
    vec3 hslColor = vec3(
        (freq1 + freq2 + freq3) * 0.5 + 0.5,
        sin(time * 0.7) * 0.5 + 0.5,
        cos(time * 0.5) * 0.5 + 0.5
    );

    // Final color transformation
    vec3 finalColor = hsl2rgb(hslColor);

    return vec4(finalColor, 1.0);
}
