vec4 Effect2(vec2 uv) {
    // Create flowing vertical stripes pattern
    float time = iTime * 0.5;

    // Vertical stripe pattern with sine wave variation
    float pattern1 = sin(uv.x * 10.0 + time) * 0.5 + 0.5;
    float pattern2 = sin(uv.x * 20.0 + time * 0.7) * 0.3 + 0.3;

    // Combine patterns with turbulence
    float mixValue = (pattern1 + pattern2) * 0.8;
    mixValue += turbulent(vec3(uv.x, time, 0.0)) * 0.2;

    // Create smooth color transitions using HSL to RGB conversion
    vec3 mainColor = hsl(mixValue * 0.6 + 0.4, 1.0, 0.5);
    vec3 secondaryColor = hsl(mixValue * 0.6 + 0.8, 1.0, 0.5);

    // Interpolate between colors based on position and time
    float colorMix = sin(uv.x * 2.0 + time) * 0.5 + 0.5;
    vec3 finalColor = mix(mainColor, secondaryColor, colorMix);

    return vec4(finalColor, 1.0);
}
