vec4 Effect2(vec2 uv) {
    // Create wave patterns with different frequencies and phases
    float wave1 = sin(uv.x * 8.0 + iTime) * cos(uv.y * 4.0);
    float wave2 = sin(uv.y * 6.0 - iTime) * cos(uv.x * 3.0);
    float wave3 = sin((uv.x + uv.y) * 5.0 + iTime * 2.0);

    // Combine waves with different phase shifts
    vec3 waves = vec3(
        wave1 + sin(iTime * 0.5 + 1.0),
        wave2 + cos(iTime * 0.6 + 2.0),
        wave3 + sin(iTime * 0.7 + 3.0)
    );

    // Add turbulence for more dynamic patterns
    vec2 noiseUV = uv * 4.0 + iTime;
    float turb = turbulent(noiseUV);

    // Create color transitions using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.1 + waves.r, 1.0),
        0.7,
        0.5 + turb * 0.5
    ));

    // Combine everything with some blending
    vec3 finalColor = (waves * 0.5 + 0.5) * color;

    return vec4(finalColor, 1.0);
}
