vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Create hexagonal grid pattern
    theta += iTime * 0.2; // Rotate over time
    float angleSegment = pi / 3.0;
    float segment = mod(theta, angleSegment) / angleSegment;

    // Calculate color with smooth transitions
    vec3 baseColor = hsl2rgb(vec3(0.5 + 0.5 * sin(iTime * 0.1), 0.7, 0.6));
    vec3 secondaryColor = hsl2rgb(vec3(0.5 + 0.5 * cos(iTime * 0.1), 0.5, 0.4));

    // Mix colors based on segment
    vec3 color = mix(baseColor, secondaryColor, sin(segment * pi) * 0.5 + 0.5);

    // Add noise for detail
    float noiseValue = noise(uv * 10.0 + iTime);
    color = mix(color, baseColor, noiseValue * 0.3);

    return vec4(color, 1.0);
}
