vec4 Effect2(vec2 uv) {
    // Create base frequency components
    float freq1 = sin(uv.x * 5.0 + iTime) * cos(uv.y * 3.0 + iTime);
    float freq2 = sin(uv.y * 4.0 - iTime) * cos(uv.x * 6.0 - iTime);

    // Combine frequencies with noise
    vec2 noiseUV = uv * 5.0;
    float noise1 = noise(noiseUV + vec2(iTime, 0.0));
    float noise2 = noise(noiseUV + vec2(-iTime, 0.0));

    // Create wave patterns with time-based movement
    float wave1 = sin(uv.x * 10.0 + iTime * 2.0) * cos(uv.y * 10.0 + iTime);
    float wave2 = cos(uv.x * 8.0 - iTime * 3.0) * sin(uv.y * 6.0 - iTime);

    // Combine all components
    vec3 colorMix = vec3(
        (freq1 + noise1 + wave1) * 0.5 + 0.5,
        (freq2 + noise2 + wave2) * 0.5 + 0.5,
        turbulent(vec2(uv.x * 4.0, uv.y * 4.0) + iTime)
    );

    // Convert to HSL and back to RGB for color shifting
    vec3 hslColor = vec3(
        (colorMix.r + colorMix.g + colorMix.b) * 0.6,
        abs(colorMix.x - colorMix.y) * 2.0,
        0.5
    );

    return vec4(hsl2rgb(hslColor), 1.0);
}
