vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and create grid cells
    vec2 st = floor(uv * 100.0);

    // Create base color using HSL with time-based hue shift
    vec3 baseColor = hsl(st.x + sin(iTime)*50.0, 
                         st.y + cos(iTime*0.7)*50.0,
                         0.5);

    // Add dynamic turbulence based on position and time
    float turb = turbulent(vec2(uv * 10.0 + iTime));

    // Combine base color with turbulence for shifting effect
    vec3 finalColor = hsl2rgb(vec3(
        (baseColor.r + turb) * 0.5,
        (baseColor.g + turb) * 0.7,
        0.6
    ));

    return vec4(finalColor, 1.0);
}
