vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add time offset for animation
    vec2 st = uv * 4.0 + iTime;

    // Create two wave components with different frequencies
    float wave1 = sin(st.x + sin(iTime)) * cos(st.y);
    float wave2 = cos(st.x - cos(iTime)) * sin(st.y);

    // Combine waves and add turbulence for more complex patterns
    float combined = (wave1 + wave2) * 0.5;
    combined += turbulent(vec3(uv * 2.0, iTime * 0.1));

    // Create a color based on the wave pattern
    vec3 color = hsl(
        mod(iTime * 0.2 + combined * 0.5, 1.0),  // Hue variation over time and space
        0.7,                                      // Saturation
        0.6 + abs(combined) * 0.4                 // Brightness modulation
    );

    return vec4(hsl2rgb(color), 1.0);
}
