vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates centered at 0.5
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add rotation over time

    // Create rotating circular patterns using sine waves
    float pattern1 = sin(theta * 4.0 + iTime * 2.0) * 0.5 + 0.5;
    float pattern2 = sin(r * 8.0 - iTime) * 0.3 + 0.7;

    // Combine patterns with noise for more complexity
    float noiseValue = noise(vec2(uv.x * 10.0, uv.y * 10.0)) * 0.5 + 0.5;
    float finalPattern = pattern1 * pattern2 * noiseValue;

    // Create pulsating color effect using HSL
    vec3 hslColor = vec3(
        mod(theta * 4.0 + iTime, 1.0), // Hue variation over time and angle
        1.0,                            // Full saturation
        finalPattern                    // Brightness based on pattern
    );

    // Convert HSL to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
