vec4 Effect2(vec2 uv) {
    // Center and scale UV coordinates
    vec2 st = uv * 2.0 - 1.0;

    // Create polar coordinates
    float angle = atan2(st.y, st.x) + iTime; // Add time for rotation
    float radius = length(st);

    // Create spiral pattern using polar coordinates
    float spiral = sin(angle + radius * 5.0) * 0.5 + 0.5;

    // Add noise and turbulence for more dynamic effect
    vec3 color = hsl2rgb(vec3(
        (angle * 0.15 + iTime * 0.1) * 360.0, // Hue variation with time
        0.7, // Saturation
        0.5 + turbulent(vec3(st.x*0.5, st.y*0.5, iTime)) * 0.2 // Lightness modulation
    ));

    // Combine spiral pattern with color
    vec3 finalColor = mix(color, hsl2rgb(vec3(0.0, 0.0, 1.0)), spiral);

    return vec4(finalColor, 1.0);
}
