vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 8.0 + iTime;

    // Create a noise-based value with turbulence
    float n = turbulent(st);

    // Base color using HSL conversion
    vec3 baseColor = hsl2rgb(vec3(
        sin(st.x * 0.5 + iTime) * 0.5 + 0.5,  // Hue
        0.7,                                    // Saturation
        0.5 + n * 0.2                           // Lightness
    ));

    // RGB shift based on time and position
    vec3 shiftedColor = vec3(
        sin(iTime + st.x) * baseColor.z,
        sin(iTime + st.y) * baseColor.r,
        sin(iTime + st.x + st.y) * baseColor.g
    );

    // Combine colors with noise modulation
    vec3 finalColor = mix(baseColor, shiftedColor, abs(sin(n * 2.0 + iTime)));

    return vec4(finalColor, 1.0);
}
