vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for rotation
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan(st.y, st.x);
    float radius = length(st);

    // Create rotating pattern using sine waves and noise
    float timeSpeed = iTime * 2.0;
    float pattern = sin(angle + timeSpeed) * 4.0;
    pattern += turbulent(vec3(uv * 10.0, iTime)) * 2.0;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(radius * 5.0 + iTime, 1.0),
        0.7,
        0.6
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Combine pattern and color with smooth transitions
    float transition = sin(iTime) * 0.5 + 0.5;
    vec3 finalColor = mix(
        rgbColor,
        vec3(1.0, 0.5, 0.2),
        abs(pattern) * 0.5 + transition * 0.5
    );

    return vec4(finalColor, 1.0);
}
