vec4 Effect2(vec2 uv) {
    // Center coordinates and scale
    vec2 st = (uv - 0.5) * 4.0;

    // Polar coordinates
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Time-based animation
    float time = iTime * 0.5;

    // Color transitions using sine functions
    vec3 color = hsl2rgb(vec3(
        0.5 * sin(angle + time) + 0.5,   // Hue
        0.7 + 0.3 * sin(radius - time), // Saturation
        0.5 + 0.5 * sin(time + radius * 0.1) // Lightness
    ));

    // Add noise for texture
    float n = noise(uv * 4.0 + iTime);
    color += vec3(0.2 * n);

    return vec4(color, 1.0);
}
