vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Convert UV coordinates to polar form for rotation
    vec2 st = uv - 0.5;
    float theta = atan2(st.y, st.x) + time;
    float radius = length(st);

    // Create rotating geometric patterns using sine waves
    float pattern1 = sin(theta * 6.0) * 0.5 + 0.5;
    float pattern2 = sin(theta * 4.0 + time) * 0.3 + 0.7;
    float pattern3 = sin(theta * 2.0 - time) * 0.2 + 0.8;

    // Combine patterns with turbulence for dynamic effect
    float combined = (pattern1 + pattern2 + pattern3) / 3.0;
    combined += turbulent(uv * 4.0 + time) * 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        fract(time * 0.2 + radius * 0.7),
        0.8,
        0.6 + turbulent(uv * 2.0 + time) * 0.4
    );

    // Convert to RGB and mix with patterns
    vec3 color = hsl2rgb(hslColor);
    color *= combined;

    // Add depth variation
    float depth = turbulent(uv * 8.0 + time * 2.0) * 0.5 + 0.5;
    color = mix(color, vec3(1.0), depth * 0.4);

    return vec4(color, combined * 0.8 + depth * 0.2);
}
