vec4 Effect2(vec2 uv) {
    // Animate pattern over time
    float time = iTime * 0.5;

    // Create intersecting sine wave patterns
    vec2 st1 = uv * 10.0 + vec2(time, 0.0);
    vec2 st2 = uv * 10.0 + vec2(0.0, time);

    // Generate wave patterns with noise and turbulence
    float wave1 = sin(st1.x) * cos(st1.y) * 0.5;
    float wave2 = sin(st2.y) * cos(st2.x) * 0.5;

    // Combine waves and add turbulence
    float pattern = (wave1 + wave2) * 0.5;
    pattern += turbulent(uv * 4.0 + vec2(time)) * 0.3;

    // Create shifting hues using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(0.6 + time * 0.1, 1.0, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.3 - time * 0.1, 1.0, 0.5));

    // Mix colors based on pattern
    vec3 finalColor = mix(color1, color2, abs(pattern) * 0.5 + 0.5);

    // Add pulsing effect to the overall brightness
    float pulse = sin(time * 2.0) * 0.5 + 0.5;
    finalColor *= pulse;

    return vec4(finalColor, 1.0);
}
