vec4 Effect2(vec2 uv) {
    // Time-based animation parameters
    float time = iTime * 0.5; // Speed control

    // Create sine wave patterns with different frequencies
    float pattern1 = sin(uv.x * 8.0 + time) * cos(uv.y * 4.0 + time);
    float pattern2 = sin(uv.y * 6.0 - time) * cos(uv.x * 3.0 + time);

    // Combine patterns with intensity
    float combined = (pattern1 + pattern2) * 2.0;
    combined += turbulent(vec3(uv * 4.0, time)) * 0.5; // Add turbulence

    // Create color shifts using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(sin(time) * 0.5 + 0.5, 0.7, 0.6));
    vec3 color2 = hsl2rgb(vec3(0.3 + combined * 0.2, 0.8, 0.7));

    // Blend colors based on pattern
    vec3 finalColor = mix(color1, color2, abs(sin(uv.x * 2.0 + uv.y * 3.0 + time) * 0.5 + 0.5));

    return vec4(finalColor, 1.0);
}
