vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 50.0 + iTime * 10.0;

    // Create multi-scale noise patterns
    float n1 = noise(st * 0.5);
    float n2 = noise(st * 0.75);
    float n3 = noise(st * 1.0);

    // Combine noise layers with different frequencies
    vec3 color = vec3(
        sin(n1 * pi + iTime) * 0.5 + 0.5,
        sin(n2 * pi + iTime * 0.7) * 0.5 + 0.5,
        sin(n3 * pi + iTime * 0.4) * 0.5 + 0.5
    );

    // Add turbulence for more complex patterns
    vec2 pos = uv * 10.0;
    float turb = turbulent(pos);
    color *= abs(turb * 0.5 + 0.5);

    // Create optical illusion effect by mixing colors
    color = hsl2rgb(vec3(
        (uv.x * pi + iTime) * 0.5,
        1.0 - length(uv)*0.5,
        0.5 + turb * 0.25
    ));

    // Add global brightness variation
    float bright = sin(iTime * 0.3) * 0.5 + 0.7;
    color *= bright;

    return vec4(color, 1.0);
}
