vec4 Effect2(vec2 uv) {
    // Create a dynamic pattern based on time and mouse movement
    float time = iTime * 0.5;

    // Add mouse position influence (assuming mouse coordinates are normalized)
    vec2 mousePos = vec2(0.5, 0.5); // Replace with actual mouse input if available

    // Create a noise-based pattern with turbulence
    float pattern = sin(uv.x * 10.0 + time) * cos(uv.y * 10.0 + time);
    pattern += noise(vec3(uv * 2.0, time)) * 0.5;
    pattern += turbulent(vec3(uv * 4.0, time)) * 0.3;

    // Create shifting lines effect
    float line = sin(pattern * 10.0 + mousePos.x * 100.0) * 0.5 + 0.5;

    // Convert to HSL color with dynamic hue
    vec3 hslColor = vec3(line * 360.0, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
