vec4 Effect2(vec2 uv) {
    // Create flowing patterns using sine waves and time
    float time = iTime * 0.5;

    // Base color using HSL
    vec3 baseColor = hsl(
        sin(time + uv.x * 2.0) * 0.4 + 0.6,  // Hue variation
        0.7,                                   // Saturation
        0.5                                    // Lightness
    );

    // Create flowing pattern using sine waves and noise
    float wave1 = sin(uv.x * 8.0 + time) * 0.5;
    float wave2 = sin(uv.y * 4.0 + time * 0.7) * 0.3;
    float wave3 = turbulent(vec2(uv.x * 2.0, uv.y * 3.0 + time)) * 0.2;

    // Combine waves with alpha blending
    float blend = sin(wave1 + wave2 + wave3 + time) * 0.5 + 0.5;
    vec4 color = vec4(baseColor, blend);

    // Add depth using noise and turbulence
    float depth = turbulent(vec3(uv.x * 2.0, uv.y * 2.0, time)) * 0.3;
    color.rgb += hsv2rgb(vec3(sin(time + uv.y) * 0.5 + 0.5, 1.0, 0.5)) * depth;

    // Soften edges using smoothstep
    float edge = smoothstep(0.0, 1.0, blend);
    color.a *= edge;

    return color;
}
