vec4 Effect2(vec2 uv) {
    // Create pulsing effect by scaling UV coordinates over time
    vec2 st = uv * 10.0;
    float scale = sin(iTime * 0.5 + length(uv)) * 0.5 + 0.5;
    vec2 pos = (st - floor(st)) * scale + fract(vec2(iTime) * 0.3);

    // Generate grid pattern with noise
    float noiseVal = noise(pos * 0.5 + iTime);
    float grid = abs(sin(pos.x * pi) * sin(pos.y * pi));

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.1 + pos.x * 0.5, 1.0),
        0.7,
        0.6
    ));

    // Add turbulence for dynamic movement
    float turb = turbulent(pos * 0.5 + iTime);
    color *= grid * (1.0 + turb * 0.3);

    return vec4(color, 1.0);
}
