vec4 Effect2(vec2 uv) {
    // Create smooth waves with varying frequencies and phases
    float time = iTime * 0.5;

    // Wave pattern calculations
    vec3 wave1 = sin(uv.xxy * 8.0 + time * 2.0);
    vec3 wave2 = cos(uv.yyx * 4.0 + time * 3.0);
    vec3 waves = (wave1 + wave2) * 0.5;

    // Add turbulence for more complex patterns
    float turb = turbulent(uv * 4.0 + time);
    waves += turb * 0.5;

    // Create color transitions using HSL
    vec3 color1 = hsl2rgb(vec3(0.6, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(0.2, 0.8, 0.7));
    vec3 finalColor = mix(color1, color2, waves.x * 0.5 + 0.5);

    // Add lighting effects
    float light = length(uv - 0.5) * 2.0;
    light = mod(light, 1.0) * 0.5 + 0.5;
    finalColor *= light;

    return vec4(finalColor, 1.0);
}
