vec4 Effect2(vec2 uv) {
    // Create flowing sine wave patterns with time-based movement
    float time = iTime * 0.5;

    // Base pattern using sine waves
    vec2 st = uv * 10.0;
    float pattern = sin(st.x + time) * 0.5 + 0.5;
    pattern += sin(st.y + time * 0.7) * 0.3;

    // Add noise and turbulence for more dynamic patterns
    vec2 noisePos = uv * 10.0 + vec2(time);
    float n = noise(noisePos) * 0.5 + 0.5;
    pattern += turbulent(noisePos) * 0.3;

    // Create color transitions using HSL to RGB conversion
    vec3 hslColor;
    hslColor.x = (time * 0.1) + mod(pattern, 1.0); // Changing hue over time
    hslColor.y = 0.7; // Saturation
    hslColor.z = pattern * 0.5 + 0.2; // Brightness

    vec3 color = hsl2rgb(hslColor);

    // Add secondary color component for more depth
    float secondaryPattern = sin(uv.x * 10.0 - time) * 0.5 + 0.5;
    vec3 secondaryColor = hsl2rgb(vec3((time * 0.1) + 0.5, 0.7, pattern * 0.5 + 0.2));

    // Combine patterns and colors
    color = mix(color, secondaryColor, secondaryPattern);

    return vec4(color, 1.0);
}
