vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Create rotating color wheel effect
    float time = iTime * 0.5;
    theta += time;
    // Convert polar coordinates to HSL color space
    vec3 hslColor = vec3(theta * 0.16 + 0.5, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);
    // Add pulsing glow effect based on radius and time
    float pulse = sin(time * 4.0) * 0.5 + 0.5;
    float glowFactor = smoothstep(1.0 - pulse, 1.0 + pulse, r);

    // Combine color with glow effect
    vec3 finalColor = rgbColor * glowFactor;
    return vec4(finalColor, 1.0);
}
