vec4 Effect2(vec2 uv) {
    // Center and scale UV coordinates
    vec2 st = uv * 2.0 - 1.0;

    // Create rotating pattern using polar coordinates
    float angle = atan2(st.y, st.x) + iTime * 0.5;
    float radius = length(st);

    // Add noise and turbulence
    float noiseVal = turbulent(vec3(st * 4.0, iTime)) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 baseColor = hsl(angle * 2.0, 1.0, 0.6);
    vec3 finalColor = mix(baseColor, vec3(1.0), noiseVal);

    // Add geometric elements
    float pattern = sin(radius * 8.0 + iTime) * 0.5 + 0.5;
    finalColor *= pattern;

    return vec4(finalColor, 1.0);
}
