vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float r = length(st);

    // Create a radial glow effect
    float glow = 1.0 - r*r;
    glow *= turbulent(vec3(r*5.0 + iTime, angle*5.0, 0.0));

    // Add smile shape
    float mouth = sin(angle * 4.0 + iTime * 2.0) * (1.0 - r*r);
    float smile = max(0.0, glow + mouth * 0.5);

    // Color transformation using HSL to RGB
    vec3 color = hsl2rgb(vec3(
        angle * 0.5 + iTime,
        0.7,
        0.5 + smile * 0.5
    ));

    return vec4(color, 1.0);
}
