vec4 Effect2(vec2 uv) {
    // Create pulsating pattern using time and noise
    float time = iTime * 0.5;

    // Generate grid lines with varying thickness
    vec2 st = uv * 10.0; // Scale up for more squares

    // Horizontal line
    float hline = step(0.4, sin(time + st.x) * 0.5 + 0.5);

    // Vertical line
    float vline = step(0.4, sin(time + st.y) * 0.5 + 0.5);

    // Combine lines to form squares
    vec2 lines = vec2(hline, vline);

    // Add noise and turbulence for dynamic movement
    vec3 noiseColor = hsl(
        (turbulent(vec3(st.x + time, st.y + time, 0.0)) * 0.5 + 0.5) * 360.0,
        1.0,
        0.7
    );

    // Convert HSL to RGB and mix with background
    vec3 color = hsl2rgb(noiseColor);
    vec3 finalColor = mix(vec3(0.0), color, lines.x * lines.y);

    return vec4(finalColor, 1.0);
}
