vec4 Effect2(vec2 uv) {
    // Create time-varying coordinates
    vec2 st = uv * 10.0 + sin(iTime * 0.5) * 3.0;

    // Generate noise values with different scales
    float n1 = noise(st + iTime * 0.5);
    float n2 = turbulent(st * 0.5 + iTime * 0.7);

    // Combine noise and time for color transitions
    float value = sin(n1 * 3.0 + iTime) * 0.5 + 0.5;
    value += sin(n2 * 2.0 + iTime * 0.3) * 0.3;

    // Create smooth color transitions using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        (value * 360.0 + iTime * 180.0) / 360.0,   // Hue
        0.7 + sin(iTime * 0.5) * 0.2,               // Saturation
        0.5 + cos(iTime * 0.4) * 0.3               // Lightness
    ));

    return vec4(color, 1.0);
}
