vec4 Effect2(vec2 uv) {
    float frequency = 8.0;
    float speed = 1.5;

    // Create rotating pattern using sine and cosine functions
    vec2 st = uv * frequency;
    float time = iTime * speed;

    // Calculate oscillating values for x and y directions
    float xOsc = sin(st.x + time) * cos(time);
    float yOsc = cos(st.y - time) * sin(time);

    // Combine x and y oscillations with different phase shifts for RGB channels
    vec3 color;
    color.r = sin(xOsc + yOsc + time) * 0.5 + 0.5;
    color.g = sin(xOsc - yOsc + time + (2.0 * PI)/3.0) * 0.5 + 0.5;
    color.b = sin(xOsc + yOsc + time + (4.0 * PI)/3.0) * 0.5 + 0.5;

    // Add turbulence to create more dynamic pattern
    vec2 noiseUV = uv * 10.0;
    float turbulence = turbulent(noiseUV);
    color *= 1.0 + turbulence * 0.5;

    return vec4(color, 1.0);
}
