vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float theta = atan2(pos.y, pos.x);
    // Create shifting lines pattern
    float linePattern = sin(theta * 8.0 + iTime) * 0.5 + 0.5;

    // Add depth effect using perspective projection
    vec3 color = hsl(
        (theta / PI + 1.0) * 0.5,   // Hue based on angle
        0.7 + linePattern * 0.3,     // Saturation modulation
        0.5 + noise(vec2(r * 4.0, iTime)) * 0.5  // Value with noise
    );

    // Convert to RGB and apply perspective distortion
    color = hsl2rgb(color) / (r * 3.0 + 1.0);

    return vec4(color, 1.0);
}
