vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates relative to center
    vec2 st = uv - 0.5;
    float r = length(st);
    float a = atan2(st.y, st.x);

    // Create rotating pattern using time and angle
    float time = iTime * 3.0;
    a += time;

    // Create expanding rings effect
    float ring = sin(r * 15.0 + time) * 0.5 + 0.5;
    ring = mod(ring, 1.0);

    // Use angle to create rainbow colors
    vec3 color = hsl(a / (2.0 * PI), 1.0, 0.5);

    // Add pulsing effect to rings
    float pulse = sin(time * 0.5 + r * 10.0) * 0.5 + 0.5;
    color = mix(color, vec3(1.0), pulse);

    // Combine ring pattern with color
    color *= ring;

    return vec4(color, 1.0);
}
