vec4 Effect2(vec2 uv) {
    // Create a base color using HSL
    vec3 baseColor = hsl2rgb(vec3(0.5 + sin(iTime * 0.5) * 0.5, 1.0, 0.8));

    // Add sine wave modulation based on time and position
    float freq = 4.0;
    vec3 sineMod = vec3(
        sin(uv.x * freq * 2.0 + iTime) * 0.5 + 0.5,
        sin(uv.y * freq * 3.0 + iTime * 0.7) * 0.5 + 0.5,
        sin((uv.x + uv.y) * freq * 4.0 + iTime * 1.2) * 0.5 + 0.5
    );

    // Add turbulence effect
    vec3 turb = vec3(turbulent(uv * 8.0 + vec2(iTime)), 
                    turbulent(uv * 6.0 + vec2(iTime * 0.7)),
                    turbulent(uv * 4.0 + vec2(iTime * 1.2)));

    // Combine effects
    vec3 finalColor = mix(baseColor, sineMod, 0.5) + turb * 0.3;
    finalColor = clamp(finalColor, 0.0, 1.0);

    return vec4(finalColor, 1.0);
}
