vec4 Effect2(vec2 uv) {
    // Create a base pattern using sine waves with time-based modulation
    float speed = 0.5;
    vec2 st = uv * 10.0; // Scale up coordinates for more detailed patterns

    // Add time-based oscillation
    float timeMod = sin(iTime * speed) * 0.5 + 0.5;

    // Create a pattern using sine functions in both x and y directions
    float patternX = sin(st.x * 2.0 + iTime * speed) * 0.5 + 0.5;
    float patternY = sin(st.y * 2.0 + iTime * speed * 0.7) * 0.5 + 0.5;

    // Combine patterns and add turbulence
    float combined = (patternX + patternY) * 0.5;
    combined += turbulent(vec3(uv * 10.0, iTime)) * 0.2; // Add some randomness

    // Create a color shift effect using HSL colorspace
    vec3 hslColor = vec3(
        sin(iTime * 0.3 + uv.x * 5.0) * 0.5 + 0.5, // Hue variation
        0.7, // Saturation
        combined * 0.6 + 0.4 // Lightness based on pattern
    );

    // Convert HSL to RGB and add some RGB variations
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor += sin(vec3(uv.x, uv.y, iTime) * 15.0 + vec3(0.0, 0.7, 1.4)) * 0.2;

    // Final color with alpha
    return vec4(rgbColor, 1.0);
}
