vec4 Effect2(vec2 uv) {
    vec4 result;

    // Scale UV coordinates for pattern size
    vec2 st = uv * 50.0;

    // Time-based animation
    float time = iTime * 0.2; // Speed control

    // Color components with different frequencies
    float red = sin(st.x * 0.1 + time) * 0.5 + 0.5;
    float green = sin(st.y * 0.3 + time * 2.0) * 0.5 + 0.5;
    float blue = sin(length(st) * 0.5 + time * 4.0) * 0.5 + 0.5;

    // Add noise and turbulence for more dynamic patterns
    red += noise(st * 0.1) * 0.3;
    green += turbulent(st * 0.2) * 0.2;
    blue += noise((st.x + time)*vec2(1.0, 0.5)) * 0.4;

    // Clamp values to prevent overflow
    red = clamp(red, 0.0, 1.0);
    green = clamp(green, 0.0, 1.0);
    blue = clamp(blue, 0.0, 1.0);

    result = vec4(red, green, blue, 1.0);
    return result;
}
