vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create RGB sine waves with different phases
    vec3 color = vec3(
        sin(uv.x * 4.0 + time) * 0.5 + 0.5,
        sin(uv.y * 4.0 + time * 0.7) * 0.5 + 0.5,
        sin((uv.x + uv.y) * 3.0 - time) * 0.5 + 0.5
    );

    // Add noise and turbulence for more dynamic effect
    vec2 pos = uv * 4.0;
    color += turbulent(pos + time) * 0.3;

    // Shift hues over time
    color = hsl2rgb(vec3(
        mod(time * 0.1, 1.0),
        0.7,
        0.5
    )) * color;

    return vec4(color, 1.0);
}
