vec4 Effect2(vec2 uv) {
    // Create time-based modulation
    float t = iTime * 0.5;

    // Generate base patterns using sine waves
    vec3 color1 = sin(vec3(6.0, 7.0, 8.0) * uv.x + t);
    vec3 color2 = sin(vec3(4.0, 5.0, 6.0) * uv.y - t);

    // Add noise and turbulence
    float noise1 = noise(uv * 4.0 + t);
    float noise2 = turbulent(vec3(uv * 2.0, t));

    // Combine patterns with noise
    vec3 combined = (color1 + color2) * 0.5;
    combined += noise1 * 0.3;
    combined += noise2 * 0.4;

    // Convert to HSL and back for smooth transitions
    vec3 hslColor = hsl(0.5, 0.7, 0.6 + sin(t) * 0.3);
    vec3 finalColor = hsv2rgb(vec3(combined.x, combined.y, combined.z));

    // Mix colors and add glow effect
    finalColor = mix(finalColor, hslColor, 0.4);
    finalColor *= 1.5 - length(uv * 1.5); // Add radial falloff

    return vec4(finalColor, 1.0);
}
