vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st) * 5.0;

    // Create flowing pattern using noise and time
    float flow = noise(vec2(theta + iTime * 0.5, r * 0.1));
    flow += turbulent(vec2(theta - iTime * 0.3, r * 0.2)) * 0.5;

    // Create color transitions using HSL
    vec3 color1 = hsl2rgb(vec3(0.6 + flow * 0.4, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(0.2 + flow * 0.6, 0.7, 0.6));

    // Combine colors based on pattern
    float blend = smoothstep(0.0, 1.0, sin(r + iTime) * 0.5 + 0.5);
    vec3 finalColor = mix(color1, color2, blend);

    return vec4(finalColor, 1.0);
}
