vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for a grid pattern
    vec2 st = uv * 20.0;

    // Create integer grid coordinates
    vec2 grid = floor(st);

    // Calculate pulsation using sine function
    float pulse = sin(iTime + grid.x + grid.y) * 0.5 + 0.5;

    // Offset UV coordinates based on pulsation
    vec2 offset = (st - grid) - 0.5;
    offset *= 1.0 + pulse * 2.0;

    // Create color transitions using HSL
    float hue = iTime * 0.1 + grid.x * 0.3 + grid.y * 0.4;
    vec3 color = hsl(hue, 0.7, 0.5);

    // Add noise for variation
    float noiseVal = noise(grid) * 0.2;
    color += noiseVal;

    // Combine pulsation and color
    vec3 finalColor = mix(vec3(0.1), color, pulse);

    return vec4(finalColor, 1.0);
}
