vec4 Effect2(vec2 uv) {
    // Scale UV coordinates and add time-based movement
    vec2 st = uv * 4.0 + iTime;

    // Create a turbulent noise pattern
    float t1 = turbulent(st);

    // Add another layer of turbulence with offset
    float t2 = turbulent(st + vec2(1.5, 3.2));

    // Combine turbulence values and scale for color transitions
    float hue = (t1 + t2 * 0.5) * 0.5;

    // Create smooth color transitions using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(hue, 0.7, 0.6));

    return vec4(color, 1.0);
}
