vec4 Effect2(vec2 uv) {
    // Create a base pattern with sine waves
    float pattern = sin(uv.y * 10.0 + iTime) * 0.5 + 0.5;

    // Add horizontal movement and create interference patterns
    pattern += sin(2.0 * uv.x - iTime * 0.5) * 0.3;

    // Use turbulence to add noise and complexity
    float noise = turbulent(vec2(uv.x * 10.0, uv.y * 10.0)) * 0.2;

    // Combine patterns and add time-based color variation
    vec3 color = hsl2rgb(vec3(
        (pattern + noise) * 0.5 + iTime * 0.1,
        0.7,
        0.6
    ));

    return vec4(color, 1.0);
}
