vec4 Effect2(vec2 uv) {
    // Scale and offset UV coordinates for a more interesting pattern
    vec2 st = uv * 8.0 + iTime;

    // Create a grid of points using fract and noise
    vec2 grid = fract(st);

    // Calculate distance from center to create circular patterns
    float dist = length(grid - 0.5);

    // Use time to animate the colors
    float hue = iTime * 0.1;

    // Create a color based on HSL values
    vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Add turbulence for dynamic noise effect
    float turb = turbulent(vec3(st, iTime)) * 0.5 + 0.5;

    // Mix colors based on distance and noise
    vec3 finalColor = mix(color, vec3(1.0), dist * turb);

    return vec4(finalColor, 1.0);
}
